settings.customCSS:
form[data-cf-form="yLta2k"] .cf-form-actions {
    display: flex;
    flex-direction: column-reverse;
    margin-top: 0 !important;
}
form[data-cf-form="yLta2k"] .cf-form-actions button.cf-cancel {
    color: #73788a;
    margin-top: 20px;
    background-color: transparent;
    width: auto;
    display: inherit;
}
form[data-cf-form="yLta2k"] .cf-form-actions button.cf-submit-form {
    width: 100%;
    display: block !important;
    position: relative !important;
    z-index: 1;
    border-radius: 25px;
    overflow: hidden;
    margin: 0 auto !important;
    box-shadow: 0 5px 30px 0 rgb(3 216 222 / 20%) !important;
    -moz-box-shadow: 0 5px 30px 0 rgba(3,216,222,.2);
    -webkit-box-shadow: 0 5px 30px 0 rgb(3 216 222 / 20%);
    -o-box-shadow: 0 5px 30px 0 rgba(3,216,222,.2);
    -ms-box-shadow: 0 5px 30px 0 rgba(3,216,222,.2);
}
form[data-cf-form="yLta2k"] .cf-form-actions button.cf-submit-form > span {
    font-size: 16px;
    color: #fff;
    line-height: 1.2;
    text-transform: uppercase;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 20px;
    width: 100%;
    height: 50px;
    outline: 0!important;
    border: none;
    background: #0051ba;
    max-width: 470px;
    -webkit-transition: all .4s;
    -o-transition: all .4s;
    -moz-transition: all .4s;
    transition: all .4s;
}
form[data-cf-form="yLta2k"] input:focus-visible {
    outline: -webkit-focus-ring-color auto 1px;
}
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--envelope,
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--server,
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--lock {
    position: relative;
}
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--envelope::before,
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--server::before,
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--lock::before {
    content: "";
    position: absolute;
    bottom: calc(0.5em + 20px);
    left: calc(0.5em + 27px);
    background-image: url("https://cdn.shopify.com/s/files/1/0273/7133/9864/files/icon--envelope.png?v=1636965882");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    width: 15px;
    height: 15px;
}
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--server::before {
    background-image: url("https://cdn.shopify.com/s/files/1/0273/7133/9864/files/icon--server.png?v=1636965881");
}
form[data-cf-form="yLta2k"] .cf-field.my-form.icon--lock::before {
    background-image: url("https://cdn.shopify.com/s/files/1/0273/7133/9864/files/icon--lock.png?v=1636965882");
}
